/// <reference path="../typings/globals/underscore/index.d.ts" />
/** @module util/data */

import { camelize } from './string'

export {
  titles,
  genders,
  maritalStatuses,
  nationalities
}

/**
 * @function titles
 * @export
 * @summary provides a list of name titles
 * @param {boolean} [flatten=false] whether to return an array of plain text values
 * @returns {object[]|string[]} If flatten is true, an array of string values otherwise an array of objects with name and camelized value
 */
function titles(flatten = false): object[] | string[] {
  const titles = ['Miss', 'Mr', 'Mrs', 'Ms', 'Mx', 'Dr', 'Master', 'Sir', 'Prof']

  if (flatten) {
    return titles.map(title => camelize(title))
  }

  return titles.map(function appendValue(name) {
    return {
      name,
      value: camelize(name)
    }
  })
}

/**
 * @function genders
 * @export
 * @summary provides a list of genders
 * @param {boolean} [flatten=false] whether to return an array of plain text values
 * @returns {object[]|string[]} If flatten is true, an array of string values otherwise an array of objects with name and camelized value
 */
function genders(flatten = false): object[] | string[] {
  const genders = ['Female', 'Male', 'Unspecified']

  if (flatten) {
    return genders.map(gender => camelize(gender))
  }

  return genders.map(function appendValue(name) {
    return {
      name,
      value: camelize(name)
    }
  })
}

/**
 * @function maritalStatuses
 * @export
 * @summary provides a list of maritalStatuses
 * @param {boolean} [flatten=false] whether to return an array of plain text values
 * @returns {object[]|string[]} If flatten is true, an array of string values otherwise an array of objects with name and camelized value
 */
function maritalStatuses(flatten = false): object[] | string[] {
  if (flatten) {
    return ['single', 'married', 'widowed', 'divorced']
  }

  return [
    { name: 'Single', value: 'single' },
    { name: 'Married/Civil Partnership', value: 'married' },
    { name: 'Widowed', value: 'widowed' },
    { name: 'Divorced', value: 'divorced' }
  ]
}

