declare type PrimitiveConstructor = StringConstructor | ObjectConstructor | BooleanConstructor | NumberConstructor | DateConstructor | ArrayConstructor | RegExp

declare namespace Schema {
  export interface IOptional {
    type: PrimitiveConstructor,
    optional: boolean
  }

  export interface IBlackbox {
    type: PrimitiveConstructor,
    blackbox: boolean
  }

  export interface IOptionalBlackbox {
    type: PrimitiveConstructor,
    optional: boolean,
    blackbox: boolean
  }

  export interface ICreatedDate {
    type: PrimitiveConstructor
    autoValue: Function
  }

  export interface IUpdatedDate {
    type: PrimitiveConstructor
    autoValue: Function
  }

  export interface IGeoJSONInner {
    'type': StringConstructor
    'coordinates': {
      type: ArrayConstructor
      maxCount: number
      minCount: number
    }
    'coordinates.$': {
      type: NumberConstructor
      decimal: boolean
    }
  }

  export interface IGeoJSON {
    type: IGeoJSONInner
    optional: Boolean
  }
}